package com.example.hw3_m3;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.hw3_m3.databinding.FragmentFirstBinding;

import java.util.ArrayList;


public class FirstFragment extends Fragment {

    private FragmentFirstBinding binding;
    private ArrayList<Car> cars = new ArrayList<>();

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        binding = FragmentFirstBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        CarAdapter adapter = new CarAdapter(cars);
        binding.rvCars.setAdapter(adapter);
        initAdapter();
    }

    private void initAdapter() {
        cars.add(new Car("BMW", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT1lmjL8lavfaLdnxIRHhhpTl3gN8LxmQ7f8w&s", "The history of the BMW Group reaches back to 1916. Today, the four brands BMW, MINI, Rolls-Royce, and BMW Motorrad make the BMW Group one of the world’s leading premium suppliers of automobiles, motorcycles, as well as mobility services. Since the beginning of its history, the company has kept its sight firmly set on the future and has consequently put sustainability and efficient resource management into the centre of its strategy. Learn more about the history and historical focus of the BMW Group."));
        cars.add(new Car("Mersedes", "data:image/jpeg;base64,", "Mercedes-Benz (German pronunciation: [mɛʁˌtseːdəs ˈbɛnts, -dɛs -] ⓘ),[6][7] commonly referred to simply as Mercedes and occasionally as Benz, is a German luxury and commercial vehicle brand that was founded in 1926. Mercedes-Benz AG (a subsidiary of the Mercedes-Benz Group, established in 2019) is based in Stuttgart, Baden-Württemberg, Germany.[1] Mercedes-Benz AG manufactures luxury vehicles and light commercial vehicles, all branded under the Mercedes-Benz name. From November 2019 onwards, the production of Mercedes-Benz-branded heavy commercial vehicles (trucks and buses) has been managed by Daimler Truck, which separated from the Mercedes-Benz Group to form an independent entity at the end of 2021."));
        cars.add(new Car("Audi", "data:image/jpeg;base64,", "Audi AG (German: [ˈaʊ̯di ʔaːˈɡeː] ⓘ) is a German automotive manufacturer of luxury vehicles headquartered in Ingolstadt, Bavaria, Germany. A subsidiary of the Volkswagen Group, Audi produces vehicles in nine production facilities worldwide.\n" +
                "\n" +
                "The origins of the company are complex, dating back to the early 20th century and the initial enterprises (Horch and the Audiwerke) founded by engineer August Horch. Two other manufacturers (DKW and Wanderer) also contributed to the foundation of Auto Union in 1932. The modern Audi era began in the 1960s, when Auto Union was acquired by Volkswagen from Daimler-Benz.[9] After relaunching the Audi brand with the 1965 introduction of the Audi F103 series, Volkswagen merged Auto Union with NSU Motorenwerke in 1969, thus creating the present-day form of the company."));
        cars.add(new Car("Honda", "data:image/jpeg;base64,", "Honda has been the world's largest motorcycle manufacturer since 1959,[3][4] reaching a production of 400 million by 19 December 2019.[5] It is also the world's largest manufacturer of internal combustion engines measured by volume, producing more than 14 million internal combustion engines each year.[6] Honda became the second-largest Japanese automobile manufacturer in 2001.[7][8] In 2015, Honda was the eighth largest automobile manufacturer in the world.[9]"));
        cars.add(new Car("Toyota", "data:image/jpeg;base64,", "Toyota Motor Corporation (Japanese: トヨタ自動車株式会社, Hepburn: Toyota Jidōsha kabushikigaisha, IPA: [toꜜjota], English: /tɔɪˈjoʊtə/, commonly known as simply Toyota) is a Japanese multinational automotive manufacturer headquartered in Toyota City, Aichi, Japan. It was founded by Kiichiro Toyoda and incorporated on August 28, 1937. Toyota is the largest automobile manufacturer in the world, producing about 10 million vehicles per year." ));
    }
}
